  const moveHandler = () => {
    scrollRef.current.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div id="app-container">
      <div> <MenuBar move={moveHandler}/> </div>
      <div id="app">
        <div id="app-header">
          test
        </div>

        <Project ref={Project} />
      </div>

    </div>
  );
}

export default App;
